import React from 'react';
import {
  BrowserRouter,
  Route,
  Routes,
  Navigate,
  useLocation,
  useNavigate
} from 'react-router-dom';
import Login from './containers/login';
import Home from './containers/home';
import Error from './components/error';

const PrivateRoute = ({ children }) => {
  const Child = chil
  const employeeId = JSON.parse(window.sessionStorage.getItem('employeeId'));
  const location = useLocation();
  const navigate = useNavigate();
  return employeeId ? <children />: <Navigate to="/" />;
};

export default () => (
  <BrowserRouter>
    <div className="h-100 w-100">
      <Routes>
        <Route index path="/" element={<Login location={location} navigate={navigate} />} />
        <Route path="home" element={
          <PrivateRoute>
            <Home location={location} navigate={navigate} />
          </PrivateRoute>
        } />
        <Route path="*" element={<Error />} />
      </Routes>
    </div>
  </BrowserRouter>
);
