import React, { useState, useId } from "react";
import HomeComponent from "../components/home";
import label from "../label";
import Modal from "../components/Modal";

const Tags = [
  { key: 'feature', value: 'Feature' },
  { key: 'tech', value: 'Tech' },
];
const Home = () => {
  const [isOpen, setIsOpen] = useState(false);
  const [challenges, setchallenges] = useState(() => {
    const challenges = JSON.parse(window.sessionStorage.getItem('challenges'));
    return challenges || null;
  });
  const handleNewEntry = () => {
    setIsOpen(true);
  };
  const onSave = () => {
    const title = document.getElementById('floatingInput').value;
    const description = document.getElementById('floatingTextarea').value;
    const tag =  document.getElementById('floatingSelect').value;
    setIsOpen(false);
    const dataToSend = {
      id: useId(),
      title,
      description,
      tag
    }
    setNewChallenges([...newChallenges, dataToSend]);
    window.sessionStorage(JSON.stringify([...newChallenges, dataToSend]))
  };
  console.log()
  const onClose = () => {
    setIsOpen(false);
  };
  return (
    <>
      <HomeComponent
        handleNewEntry={handleNewEntry}
      />
      {isOpen && (
        <Modal
          title={label.ADD_IDEAS_CHALLENGES}
          onClose={onClose}
          onSave={onSave}
        >
          <div className="w-100">
            <div class="form-floating mb-3">
              <input type="text" class="form-control" id="floatingInput" placeholder="title" />
              <label for="floatingInput">Title</label>
            </div>
            <div class="form-floating mb-3">
              <textarea class="form-control" placeholder="Leave a comment here" id="floatingTextarea"></textarea>
              <label for="floatingTextarea">Comments</label>
            </div>
            <div class="form-floating mb-3">
              <select class="form-select" id="floatingSelect" aria-label="Floating label select example">
                {Tags.map(({ key, value}) => {
                  return (
                    <option value={key}>{value}</option>
                  );
                })}
              </select>
              <label for="floatingSelect">TAGS</label>
            </div>
          </div>
        </Modal>
      )}
    </>
  );
};

export default Home;
